import React, { useState } from 'react';
import { Grid, Typography, Button } from "@material-ui/core";

const Welcome = () => {
  const [isVideoPlaying, setVideoPlaying] = useState(true);

  const toggleVideo = () => {
    const video = document.getElementById('background-video');
    if (video) {
      if (isVideoPlaying) {
        video.pause();
      } else {
        video.play();
      }
      setVideoPlaying(!isVideoPlaying);
    }
  };

  return (
    <Grid
      container
      item
      direction="column"
      alignItems="center"
      justify="center"
      style={{ padding: "0", margin: "0", minHeight: "100vh", overflow: "hidden" }}
    >
      <video autoPlay muted loop id="background-video" style={{ position: "absolute", width: "100%", height: "100%", objectFit: "cover" }}>
        <source src="video.mp4" type="video/mp4" />
      </video>
      <Grid item style={{ position: "relative", zIndex: 1 }}>
        <Typography variant="h2" style={{ color: "#fff" }}>Welcome to Job Portal</Typography>
        <Button variant="contained" color="primary" onClick={toggleVideo}>
          {isVideoPlaying ? 'Pause Video' : 'Play Video'}
        </Button>
      </Grid>
    </Grid>
  );
};




export const ErrorPage = (props) => {
  return (
    <Grid
      container
      item
      direction="column"
      alignItems="center"
      justify="center"
      style={{ padding: "30px", minHeight: "93vh" }}
    >
      <Grid item>
        <Typography variant="h2">Error 404</Typography>
      </Grid>
    </Grid>
  );
};

export default Welcome;
