import { Grid, Typography } from "@material-ui/core";

const Welcome = () => {
  return (
    <>
      <head>
        <meta charset="UTF-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>auto play video</title>
      </head>
      <Grid
        container
        item
        direction="column"
        alignItems="center"
        justify="center"
        style={{
          position: "relative",
          overflow: "hidden",
          minHeight: "100vh",
          backgroundColor: "#000", // Set a background color in case the video doesn't cover the entire screen
        }}
      >
        <video
          autoPlay
          loop
          muted
          style={{
            position: "absolute",
            width: "100%",
            height: "100%",
            objectFit: "cover",
            top: 0,
            left: 0,
            zIndex: -1, // Place the video in the background
          }}
        >
          <source src="video.mp4" type="video" />
          Your browser does not support the video tag.
        </video>

        <Grid item style={{ position: "relative", zIndex: 1 }}>
          <Typography variant="h2" style={{ color: "#fff" }}>
            Welcome to Job Portal
          </Typography>
          {/* Your other content can go here */}
        </Grid>
      </Grid>
    </>
  );
};

export const ErrorPage = (props) => {
  return (
    <Grid
      container
      item
      direction="column"
      alignItems="center"
      justify="center"
      style={{ padding: "30px", minHeight: "93vh" }}
    >
      <Grid item>
        <Typography variant="h2">Error 404</Typography>
      </Grid>
    </Grid>
  );
};

export default Welcome;
