import React from 'react';
import { Grid, Typography } from "@material-ui/core";

const Welcome = () => {
  return (
    // Grid container to center content vertically and horizontally
    <Grid
      container
      item
      direction="column"
      alignItems="center"
      justify="center"
      style={{ padding: "0", margin: "0", minHeight: "100vh", overflow: "hidden" }}
    >
      {/* Video background */}
      <video autoPlay muted loop id="background-video" style={{ position: "absolute", width: "100%", height: "100%", objectFit: "cover" }}>
        <source src="video.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Content with a higher z-index to appear above the video */}
      <Grid item style={{ position: "relative", zIndex: 1 }}>
        <Typography variant="h2" style={{ color: "#fff" }}>
          Welcome to Job Portal
        </Typography>
      </Grid>
    </Grid>
  );
};

export default Welcome;
